import React from 'react';
import DisplaySimpson from './conponents/DisplaySimpson'
import axios from 'axios'


const sampleSimpson = {
  gender: 'male',
  name: {
    first: 'Charlie',
    last: 'Thompson',
  },
  location: {
    street: {
      number: 761,
      name: 'Tay Street',
    },
    city: 'Timaru',
    postcode: 76111,
  },
  email: 'charlie.thompson@example.com',
  picture: {
    medium: 'https://randomuser.me/api/portraits/med/men/40.jpg',
  },
};



class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      simpson: sampleSimpson
    };
    this.getSimpson = this.getSimpson.bind(this);
  }

  getSimpson() {
    // Send the request
    axios.get('https://simpsons-quotes-api.herokuapp.com/quotes')
      // Extract the DATA from the received response
      .then(response => response.data[0])
      // Use this data to update the state
      .then(quote => {
        this.setState({
          simpson: quote.results[0],
        });
    });
  }

  render() {
    return (
      <div className="App">
        <DisplaySimpson simpson={this.state.simpson} />
        <button type="button" onClick={this.getSimpson}>Get simpson</button>
      </div>
    );
  }
}

export default App;
