import React, { Component } from 'react';
import QuoteCard from "./conponents/QuoteCard";



class App extends Component{
  constructor(props){
    super(props);
    this.state = {
      quotes: sample
    }
  }

  getQuotes = () => { 
    const url = "https://thesimpsonsquoteapi.glitch.me/quotes";
    fetch(url)
      .then(response => response.data())
      .then(data => {
        this.setState({
        quote: data
        });
      });
  }

  render(){
    return (
      <div className="App">
        <QuoteCard quotes={this.state.quotes}/>
        <button type="button" onClick={this.getQuotes}>See another quote</button>
      </div>
    );
  }
}

export default App;
